import React from 'react';
import { Carousel } from 'react-bootstrap';
import banner1 from '../../image/banner1 (1).jpg';
import banner2 from '../../image/banner2 (1).jpg';
import banner3 from '../../image/banner3 (1).jpg';

const Banner = () => {
    return (
        <div>
            <Carousel>
                <Carousel.Item>
                    <img
                        className="d-block w-100"
                        src={banner1}
                        alt="First slide"
                    />
                    <Carousel.Caption>
                        <h3 className="text-info">Enjoy Your</h3>
                        <h1 className="fw-bold text-info">Winter Vacation</h1>
                        <p className="text-info">Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                    </Carousel.Caption>
                </Carousel.Item>
                <Carousel.Item>
                    <img
                        className="d-block w-100"
                        src={banner2}
                        alt="Second slide"
                    />

                    <Carousel.Caption>
                        <h3 className="text-secondary">Enjoy Your</h3>
                        <h1 className="fw-bold text-info">Snow adventure</h1>
                        <p className="text-info">Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                    </Carousel.Caption>
                </Carousel.Item>
                <Carousel.Item>
                    <img
                        className="d-block w-100"
                        src={banner3}
                        alt="Third slide"
                    />

                    <Carousel.Caption>
                        <h3 className="text-white">Enjoy Your</h3>
                        <h1 className="fw-bold text-white">Summer vacation</h1>
                        <p className="text-white">Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                    </Carousel.Caption>
                </Carousel.Item>
            </Carousel>
        </div>
    );
};

export default Banner;
